CREATE TABLE `sector` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


CREATE TABLE `subsector` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


CREATE TABLE `segment` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

;

CREATE TABLE `historicaldata` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `symbol` char(6) NOT NULL,
  `date` date NOT NULL,
  `open` decimal(18,4) NOT NULL,
  `high` decimal(18,4) NOT NULL,
  `low` decimal(18,4) NOT NULL,
  `close` decimal(18,4) NOT NULL,
  `adjClose` decimal(18,4) NOT NULL,
  `volume` int(11) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


CREATE TABLE `stockinfo` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `symbol` char(6) NOT NULL,
  `name` varchar(255) NOT NULL,
  `sector` varchar(255) NOT NULL,
  `subsector` varchar(255) NOT NULL,
  `segment` varchar(255) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


CREATE TABLE `symbol_code` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `symbol` varchar(255) NULL,
  `code` varchar(255) NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


CREATE TABLE `tickers` (
  `cvm` INT NOT NULL,
  `ticker` VARCHAR(12) DEFAULT NULL,
  `class` INT NOT NULL,
  PRIMARY KEY (cvm, class)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


CREATE TABLE `companies` (
  `cvm` INT NOT NULL,
  `name` VARCHAR(100) DEFAULT NULL,
  `sector` VARCHAR(100) DEFAULT NULL,
  `cnpj` CHAR(14) DEFAULT NULL,
  `segment`  CHAR(2) DEFAULT NULL,
  `situation` VARCHAR(30) DEFAULT NULL,
  `email` VARCHAR(30) DEFAULT NULL,
  `address_city` VARCHAR(30) DEFAULT NULL,
  `address_state` CHAR (2) DEFAULT NULL,
  `address_country` VARCHAR(30) DEFAULT NULL,
  PRIMARY KEY (cvm)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

CREATE TABLE `exchange` (
  `id` int NOT NULL AUTO_INCREMENT,
  `abbrev` varchar(32) NOT NULL,
  `name` varchar(255) NOT NULL,
  `city` varchar(255) NULL,
  `country` varchar(255) NULL,
  `currency` varchar(64) NULL,
  `timezone_offset` time NULL,
  `created_at` datetime NOT NULL,
  `updated_at` datetime NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;

